CREATE TABLE EMPLOYEES (EMPLOYEE_ID INT PRIMARY KEY, EMPLOYEE_NAME varchar(50) NOT NULL, GENDER varchar(50) NOT NULL, CLIENT_NAME varchar(50), MARITAL_STATUS varchar(50) NOT NULL, DEPARTMENT varchar(50),YEARS_IN_ORG INT DEFAULT 0, DATE_OF_JOINING DATE NOT NULL, PERFORMANCE_MANAGER varchar(50) NOT NULL, REPORTING_MANAGER varchar(50));
